//factorial
public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		int j =1;
		int factorial = 5;
		for(i =1;i<=factorial;i++)
		{
		  
			//System.out.println(i);
			j = j *i;
		}
	    
	    System.out.println("Factorial value till digit "+ factorial +" -->" + j);
	    System.out.println("Factorial value till digit "+ factorial +" -->" + j);
	}

}
