//addition till digit
public class Forloop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i;
		int j =0;
		int factorial = 50;
		for(i =1;i<=factorial;i++)
		{
		  
			//System.out.println(i);
			j = j +i;
		}
	    
	    System.out.println("Sum till digit "+ factorial +" -->" + j);
	
	}

}
